/**
 * アイテムデータ作成クラス.
 */
function CItemDataMaker () {

}

//================================================================
// アイテムデータ定義用ダミー関数
// （可読性を高める目的で使用する）
//================================================================
CItemDataMaker.ItemKind = function (value) { return value; };
CItemDataMaker.ItemEquipFlag = function (value) { return value; };
CItemDataMaker.ItemPower = function (value) { return value; };
CItemDataMaker.ItemWeaponLevel = function (value) { return value; };
CItemDataMaker.ItemWeaponLevelEx = function (valueLv, enchId, rndEnchId) { return enchId * 10 + valueLv + (rndEnchId != 0 ? 0.1 : 0); };
CItemDataMaker.ItemWeaponLevelExV2 = function (valueLv, enchTypeId, rndOptTypeId) { return rndOptTypeId * 10000 * 10 + enchTypeId * 10 + valueLv; };
CItemDataMaker.ItemSlot = function (valuehigh, valuelow) { return valuehigh * 10 + valuelow; };
CItemDataMaker.ItemWeight = function (value) { return value; };
CItemDataMaker.ItemEquipLevel = function (value) { return value; };
CItemDataMaker.ItemName = function (value) { return value; };
CItemDataMaker.ItemKana = function (value) { return value; };
CItemDataMaker.ItemDetailText = function (value) { return value; };

/**
 * IDを定義する.
 * @param name 定数名
 * @param value 定数値
 * @return 定数値
 */
CItemDataMaker.RegisterId = function (name, value) {
	if (name != "ITEM_ID_UNKNOWN") {
		return CGlobalConstManager.DefineEnum("EnumItemId", [name], value, undefined);
	}
	return value;
};





