/**
 * データを検査する.
 */
CItemDataMaker.CheckData = function () {

	var idx = 0;
	var idxSP = 0;

	var itemData = null;
	var spDataArray = null;

	for (idx = 0; idx < ItemObjNew.length; idx++) {

		// アイテムデータ取り出し
		itemData = ItemObjNew[idx];

		// SP情報切り出し
		spDataArray = itemData.slice(ITEM_DATA_INDEX_SPBEGIN);

		// セット定義の場合の検査
		if (itemData[ITEM_DATA_INDEX_KIND] == ITEM_KIND_SET) {

			for (idxSP = 0; idxSP < spDataArray.length; idxSP += 2) {

				// セット定義で使用不可の SPID の検査
				switch (spDataArray[idxSP]) {
				case ITEM_SP_STR_PLUS:
				case ITEM_SP_AGI_PLUS:
				case ITEM_SP_VIT_PLUS:
				case ITEM_SP_INT_PLUS:
				case ITEM_SP_DEX_PLUS:
				case ITEM_SP_LUK_PLUS:
				case ITEM_SP_ALLSTATUS_PLUS:
					alert("SP 定義ミス検出（セット用アイテム定義）" + "\n"
						+ "　アイテムID == " + EnumItemId.GetDefinedName(idx) + " (" + idx + ")" + "\n"
						+ "　NG SPID == " + EnumItemSpId.GetDefinedName(spDataArray[idxSP]) + " (" + spDataArray[idxSP] + ")"
					);
					break;
				}

			}
		}
	}
};



// データ上書き
if (_DATA_CREATE_MODE) {
	CItemDataMaker.OverrideData();
	CItemDataMaker.CheckData();
}

